mapper.insert:

	select if(isnull(FILE_CD),'COM_FILE_1', concat('COM_FILE_', MAX(CONVERT(SUBSTRING(FILE_CD,10), UNSIGNED))+1))
	as fileCode
	from COM_FILE_TB

INSERT INTO COM_FILE_TB
(
	FILE_CD
	,ARTICLE_CD
	,FILE_PATH
	,FILE_NAME
	,RANDOM_NAME
)
VALUES
(
	#{fileCode}
	,#{articleCode}
	,#{filePath}
	,#{fileName}
	,#{randomName}
)
