mapper.select:
SELECT PAYMENT_CD AS paymentCode ,CUSTOMER_CD AS customerCode ,PAYMENT_DT AS paymentDate ,ITEMDESIGN_NM AS itemDesignName ,PAYMENT_TOTAL_WON AS paymentTotalPrice ,CUSTOMER_NM AS customerName ,EMPLOYEE_NM AS employeeName ,PAYMENT_MEMO AS paymentMemo FROM CRM_PAYMENT_TB WHERE SHOP_CD = #{shopCode}
AND ITEMDESIGN_NM LIKE CONCAT('%',#{pageHelper.searchKeyword},'%')
ORDER BY
PAYMENT_DT DESC
${pageHelper.arrayKeyword} DESC
LIMIT #{pageHelper.recordStartNo}, #{pageHelper.pagePerRecordSize}

mapper.update:
UPDATE
	CRM_PAYMENT_TB
SET	
	EMPLOYEE_CD = #{employeeCode}
	,ITEMDESIGN_CD = #{itemDesignCode}
	,EMPLOYEE_NM = (SELECT EMPLOYEE_NM FROM CRM_EMPLOYEE_TB WHERE EMPLOYEE_CD = #{employeeCode})
	,ITEMDESIGN_NM = (SELECT ITEMDESIGN_NM FROM CRM_ITEM_DESIGN_TB WHERE ITEMDESIGN_CD = #{itemDesignCode}) 
	,PAYMENT_TYPE_GB = (SELECT PAYMENT_TYPE_GB FROM CRM_PAYMENT_TYPE_TB WHERE PAYMENT_TYPE_CD = #{paymentTypeCode})
	,PAYMENT_TOTAL_WON = #{paymentTotalPrice}
	,PAYMENT_DT = #{paymentDate}
	,PAYMENT_MEMO = #{paymentMemo}
WHERE
	PAYMENT_CD = #{paymentCode}
