mapper.insert:

	select if(isnull(EMPLOYEE_CD),'CRM_EMPLOYEE_1',concat('CRM_EMPLOYEE_',MAX(CONVERT(SUBSTRING(EMPLOYEE_CD,14),UNSIGNED))+1)) as employeeCode from CRM_EMPLOYEE_TB

INSERT INTO
	CRM_EMPLOYEE_TB
(
	EMPLOYEE_CD
	,SHOP_CD
	,EMPLOYEE_PW
	,EMPLOYEE_NM
	,EMPLOYEE_BIRTH_DT
	,EMPLOYEE_POST_NO
	,EMPLOYEE_ADDR
	,EMPLOYEE_PHONE_NO
	,EMPLOYEE_CELLPHONE_NO
	,EMPLOYEE_JOIN_DT
	,EMPLOYEE_MEMO
	,EMPLOYEE_LEVEL_NM
)
VALUES
(
	#{employeeCode}

	,'CRM_SHOP_4'
	,#{employeePw}
	,#{employeeName}
	,#{employeeBirthDate}
	,#{employeePostNo}
	,#{employeeAddr}
	,#{employeePhoneNo}
	,#{employeeCellPhoneNo}
	,NOW()
	,#{employeeMemo}
	,#{employeeLevelName}
)

mapper.select:
SELECT EMPLOYEE_CD as employeeCode ,SHOP_CD as shopCode ,EMPLOYEE_PW as employeePw ,EMPLOYEE_NM as employeeName ,EMPLOYEE_BIRTH_DT as employeeBirthDate ,EMPLOYEE_ADDR as employeeAddr ,EMPLOYEE_PHONE_NO as employeePhoneNo ,EMPLOYEE_CELLPHONE_NO as employeeCellPhoneNo ,EMPLOYEE_JOIN_DT as employeeJoinDate ,EMPLOYEE_MEMO as employeeMemo ,EMPLOYEE_LEVEL_NM as employeeLevelName FROM CRM_EMPLOYEE_TB
EMPLOYEE_NM like CONCAT('%',#{word},'%')
LIMIT #{pageHelper.startRow}, #{pageHelper.linePerPage}

mapper.update:
UPDATE
	CRM_EMPLOYEE_TB
SET
	EMPLOYEE_LEVEL_NM=#{employeeLevelName}
	,EMPLOYEE_PW=#{employeePw}
	,EMPLOYEE_ADDR=#{employeeAddr}
	,EMPLOYEE_PHONE_NO=#{employeePhoneNo}
	,EMPLOYEE_CELLPHONE_NO=#{employeeCellPhoneNo}
	,EMPLOYEE_MEMO=#{employeeMemo}
WHERE
	EMPLOYEE_CD=#{employeeCode}
