mapper.select:
SELECT EMPLOYEE_CD as employeeCode ,SHOP_CD as shopCode ,EMPLOYEE_PW as employeePw ,EMPLOYEE_NM as employeeName
,EMPLOYEE_ADDR as employeeAddr ,EMPLOYEE_PHONE_NO as employeePhoneNo ,EMPLOYEE_CELLPHONE_NO as employeeCellPhoneNo
,EMPLOYEE_MEMO as employeeMemo ,EMPLOYEE_LEVEL_NM as employeeLevelName FROM CRM_EMPLOYEE_TB WHERE EMPLOYEE_CD=#{employeeCode}

mapper.update:
UPDATE
	CRM_EMPLOYEE_TB
SET
	EMPLOYEE_PW=#{employeePw}
	,EMPLOYEE_NM=#{employeeName}

	,EMPLOYEE_ADDR=#{employeeAddr}
	,EMPLOYEE_PHONE_NO=#{employeePhoneNo}
	,EMPLOYEE_CELLPHONE_NO=#{employeeCellPhoneNo}

	,EMPLOYEE_MEMO=#{employeeMemo}
	,EMPLOYEE_LEVEL_NM=#{employeeLevelName}
WHERE
	EMPLOYEE_CD=#{employeeCode}
